mapper.select:
SELECT pdi.*,pdc.NAME CATALOG_NAME FROM( SELECT pdi.* FROM PHIP_DICT_ITEM pdi
AND pdi.FK_CATALOG_CODE = #{fkCatalogCode,jdbcType=VARCHAR}
AND (UPPER(pdi.NAME) LIKE #{key,jdbcType=VARCHAR} OR UPPER(pdi.CODE) LIKE #{key,jdbcType=VARCHAR})
) pdi LEFT JOIN PHIP_DICT_CATALOG pdc ON pdi.FK_CATALOG_CODE = pdc.CODE

SELECT pdi.*, pdc.NAME CATALOG_NAME FROM (SELECT CODE, NAME FROM PHIP_DICT_CATALOG WHERE ID = #{id,jdbcType=VARCHAR}) pdc INNER JOIN( SELECT pdi.* FROM PHIP_DICT_ITEM pdi
AND (UPPER(pdi.NAME) LIKE #{key,jdbcType=VARCHAR} OR UPPER(pdi.CODE) LIKE #{key,jdbcType=VARCHAR})
) pdi ON pdc.CODE = pdi.FK_CATALOG_CODE
