mapper.select:
SELECT ID FROM PHIP_XMAN_BASE_INFO PXBI


        AND PXBI.ID_NO = #{idNo,jdbcType=VARCHAR}


        AND PXBI.PATIENT_ID = #{patientId,jdbcType=VARCHAR}

SELECT * FROM PHIP_XMAN_INDEX T


        AND T.HEALTH_CARD_ID = #{healthCardId,jdbcType=VARCHAR}


        AND T.ID_NO = #{idNo,jdbcType=VARCHAR}


        AND T.TITLE = #{title,jdbcType=VARCHAR}

    AND ROWNUM=1

SELECT PXE.ID EHR_ID, PXE.XMAN_ID, PXE.PATIENT_ID, PXE.SOURCE_ID, PXI.TITLE, PXI.HEALTH_CARD_ID, PXI.ID_NO FROM( SELECT * FROM PHIP_XMAN_INDEX PXI
AND PXI.HEALTH_CARD_ID = #{healthCardId,jdbcType=VARCHAR}
AND PXI.ID_NO = #{idNo,jdbcType=VARCHAR}
AND PXI.TITLE = #{title,jdbcType=VARCHAR}
) PXI LEFT JOIN PHIP_XMAN_EHR PXE ON PXI.ID = PXE.INDEX_ID WHERE ROWNUM = 1
